package bishi;

public class TestDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d1 = 1.00;
		double d2 = 0.41;
		double d3 = 0.51;
		System.out.println("d1 - d2: " + (d1 - d2));		// 0.5900000000000001
		System.out.println("d1 - d3: " + (d1 - d3));		// 0.49
		
		byte b = (byte) 128;
		System.out.println(b);	// -128
	}

}
